$(document).ready(function () {

    $('#basic-alert').on('click', function () {
        swal('هر شخصی می تواند از کامپیوتر استفاده کند');
    });

    $('#with-title').on('click', function () {
        swal(
            'اینترنت؟',
            'آن چیز هنوز هم حدود است؟',
            'question'
        )
    });

    $('#with-title-footer').on('click', function () {
        swal({
            type: 'error',
            title: 'متاسفیم ...',
            text: 'مشکلی پیش آمد!',
            footer: '<a href>چرا این مشکل را دارم؟</a>',
        });
    });

    $('#long-content').on('click', function () {
        swal({
            imageUrl: '../../../app-assets/images/pages/robot.jpg',
            imageHeight: 1512,
            imageAlt: 'A tall image'
        });
    });

    $('#custom-html').on('click', function () {
        swal({
            title: '<i>HTML</i> <u>مثال</u>',
            type: 'info',
            html:
                'شما می توانید استفاده کنید <b>کلمه درشت</b>, ' +
                '<a href="//github.com">لینک</a> ' +
                'و سایر برچسب ها',
            showCloseButton: true,
            showCancelButton: true,
            focusConfirm: false,
            confirmButtonText:
                '<i class="ft-thumbs-up"></i> تبریک!',
            confirmButtonAriaLabel: 'عالی!',
            cancelButtonText:
                '<i class="ft-thumbs-down"></i>',
            cancelButtonAriaLabel: 'انگشت شست',
        });
    });

    $('#custom-position').on('click', function () {
        swal({
            position: 'top-end',
            type: 'success',
            title: 'کار شما ذخیره شده است',
            showConfirmButton: false,
            timer: 1500
        });
    });

    $('#confirm-dialog').on('click', function () {
        swal({
            title: 'شما مطمئن هستید؟',
            text: 'شما نمی توانید این را برگردانید!',
            type: 'warning',
            showCancelButton: true,
            confirmButtonColor: '#3085d6',
            cancelButtonColor: '#d33',
            confirmButtonText: 'بله ، آن را حذف کنید!'
          }).then(function () {
            swal('حذف!', 'پرونده شما حذف شده است!', 'success')
          }).catch(swal.noop)
    });

    $('#rtl-support').on('click', function () {
        swal({
            title: 'آیا می خواهید ادامه دهید؟',
            confirmButtonText: 'بله',
            cancelButtonText: 'خیر',
            showCancelButton: true,
            showCloseButton: true,
            target: document.getElementById('rtl-container')
        });
    });

    $('#ajax-request').on('click', function () {
        swal({
            title: 'برای اجرای درخواست آژاکس ایمیل ارسال کنید',
            input: 'email',
            showCancelButton: true,
            confirmButtonText: 'ثبت',
            showLoaderOnConfirm: true,
            preConfirm: function (email) {
              return new Promise(function (resolve, reject) {
                setTimeout(function() {
                  if (email === 'taken@example.com') {
                    swal.showValidationError('این ایمیل قبلا ثبت شده است.')
                  }
                  resolve()
                }, 2000)
              })
            },
            allowOutsideClick: false
          }).then(function (email) {
            swal({
              type: 'success',
              title: 'درخواست آژاکس به پایان رسید!',
              html: 'ایمیل ارسالی: ' + email
            })
          });
    });

    $('#chaining-modal').on('click', function () {
        swal.setDefaults({
            input: 'text',
            confirmButtonText: 'بعدی &rarr;',
            showCancelButton: true,
            progressSteps: ['1', '2', '3']
          })
          
          var steps = [
            {
              title: 'سوال 1',
              text: 'زنجیره ای از مدول سوال 2 آسان است'
            },
            'سوال 2',
            'سوال 3'
          ]
          
          swal.queue(steps).then(function (result) {
            swal.resetDefaults()
            swal({
              title: 'انجام شد!',
              html:
                'پاسخهای شما: <pre>' +
                  JSON.stringify(result) +
                '</pre>',
              confirmButtonText: 'دوست داشتني!'
            })
          }, function () {
            swal.resetDefaults()
          });
    });

    $('#dynamic-queue').on('click', function () {
        swal.queue([{
            title: 'آی پی عمومی شما',
            confirmButtonText: 'نمایش آی پی عمومی من',
            text:
              'آی پی عمومی شما دریافت می شود ' +
              'via AJAX request',
            showLoaderOnConfirm: true,
            preConfirm: function () {
              return $.get('https://api.ipify.org?format=json')
                .then(function (data) {
                  swal.insertQueueStep(data.ip)
                })
            }
          }])
    });

    $('#type-success').on('click', function () {
        swal("آفرین!", "روی دکمه کلیک کردید!", "success");
    });

    $('#type-info').on('click', function () {
        swal("اطلاعات!", "روی دکمه کلیک کردید!", "info");
    });

    $('#type-warning').on('click', function () {
        swal("هشدار!", "روی دکمه کلیک کردید!", "warning");
    });

    $('#type-error').on('click', function () {
        swal("خطا!", "روی دکمه کلیک کردید!", "error");
    });

    $('#type-question').on('click', function () {
        swal("سوال!", "روی دکمه کلیک کردید!", "question");
    });

    $('#input-text').on('click',  function () {
        swal({
            title: 'اسم شما چیست؟',
            input: 'text',
            inputPlaceholder: 'نام یا نام مستعار خود را وارد کنید',
            showCancelButton: true,
            inputValidator: function (value) {
              return !value && 'شما باید چیزی بنویسید!'
            }
          }).then(function (name) {
            swal({
              type: 'success',
              title: 'سلام, ' + name
            })
          }).catch(swal.noop)
    });

    $('#input-email').on('click', function () {
        swal({
            title: 'آدرس ایمیل ورودی',
            input: 'email',
            inputPlaceholder: 'آدرس ایمیل خود را وارد کنید',
          }).then(function (email) {
            swal({
              type: 'success',
              html: 'ایمیل وارد شده: <strong>' + email + '</strong>'
            })
          }).catch(swal.noop)
    });

    $('#input-url').on('click', function () {
        swal({
            input: 'url',
            inputPlaceholder: 'URL را وارد کنید'
          }).then(function (url) {
            swal({
              type: 'success',
              html: 'URL وارد شده است: <strong>' + url + '</strong>'
            })
          }).catch(swal.noop)
    });

    $('#input-password').on('click', function () {
        swal({
            title: 'رمز ورود خود را وارد کنید',
            input: 'password',
            inputPlaceholder: 'رمز ورود خود را وارد کنید',
            inputAttributes: {
              'maxlength': 10,
              'autocapitalize': 'off',
              'autocorrect': 'off'
            }
          }).then(function (password) {
            if (password) {
              swal({
                type: 'success',
                html: 'گذرواژه وارد شده: <strong>' + password + '</strong>'
              })
            }
          }).catch(swal.noop)
    });

    $('#input-textarea').on('click', function () {
        swal({
            input: 'textarea',
            inputPlaceholder: 'پیام خود را اینجا بنویسید',
            inputAttributes: {
              'aria-label': 'پیام خود را اینجا بنویسید'
            },
            showCancelButton: true
          }).then(function (text) {
            if (text) {
              swal(text)
            }
          }).catch(swal.noop)
    });

    $('#input-select').on('click', function () {
        swal({
            title: 'اوکراین',
            input: 'select',
            inputOptions: {
              'SRB': 'Serbia',
              'UKR': 'Ukraine',
              'HRV': 'Croatia'
            },
            inputPlaceholder: 'کشور را انتخاب کنید',
            showCancelButton: true,
            inputValidator: function (value) {
              return new Promise(function (resolve, reject) {
                if (value === 'UKR') {
                  resolve()
                } else {
                  resolve('شما باید اوکراین را انتخاب کنید :)')
                }
              })
            }
          }).then(function (result) {
            swal({
              type: 'success',
              html: 'شما انتخاب کردید: <strong>' + result + '</strong>'
            })
          }).catch(swal.noop)
    });

    $('#input-radio').on('click', function () {
        var inputOptionsPromise = new Promise(function (resolve) {
            setTimeout(function () {
              resolve({
                '#FF0000': 'قرمز',
                '#00FF00': 'سبز',
                '#0000FF': 'آبی'
              })
            }, 2000)
          })
      
          swal({
            title: 'رنگ را انتخاب کنید',
            input: 'radio',
            inputOptions: inputOptionsPromise,
            inputValidator: function (value) {
              return !value && 'شما باید چیزی را انتخاب کنید!'
            }
          }).then(function (result) {
            swal({
              type: 'success',
              html: 'شما انتخاب کردید: <strong>' + result + '</strong>'
            })
          }).catch(swal.noop)
    });

    $('#input-checkbox').on('click', function () {
        swal({
            title: 'شرایط و ضوابط',
            input: 'checkbox',
            inputValue: 1,
            inputPlaceholder: 'من با شرایط و ضوابط موافقم',
            confirmButtonText: 'ادامه <i class="fa fa-arrow-right" style="margin-left: 10px"></i>',
            inputValidator: function (result) {
              return !result && 'برای ادامه باید موافقت کنید T&amp;C'
            }
          }).then(function () {
            swal({
              type: 'success',
              text: 'شما با T&C موافقت کردید :)'
            })
          }).catch(swal.noop)
    });

    $('#input-file').on('click', function () {
        swal({
            title: 'تصویر را انتخاب کنید',
            input: 'file',
            inputAttributes: {
              'accept': 'image/*',
              'aria-label': 'Upload your profile picture'
            }
          }).then(function (file) {
            var reader = new FileReader()
            reader.onload = function (e) {
              swal({
                title: 'عکس بارگذاری شده شما',
                imageUrl: e.target.result,
                imageAlt: 'The uploaded picture'
              }).catch(swal.noop)
            }
            reader.readAsDataURL(file)
          }).catch(swal.noop)
    });

    $('#input-range').on('click', function () {
        swal({
            title: 'شما چند سال دارید؟',
            type: 'question',
            input: 'range',
            inputAttributes: {
              min: 8,
              max: 120,
              step: 1
            },
            inputValue: 25
          }).catch(swal.noop)
        })
      
        $('.input-type-multiple').on('click', function () {
          swal({
            title: 'ورودی های متعدد',
            html:
              '<input id="swal-input1" class="swal2-input" placeholder="first input field">' +
              '<input id="swal-input2" class="swal2-input" placeholder="second input field">',
            focusConfirm: false,
            preConfirm: function () {
              return [
                $('#swal-input1').val(),
                $('#swal-input2').val()
              ]
            }
          }).then(function (result) {
            swal(JSON.stringify(result))
          }).catch(swal.noop)
    });


});